import React, {useEffect, useState} from 'react';
import {Link} from 'react-router-dom';
import {FiPlus,FiArrowRight} from 'react-icons/fi'
import { Map, TileLayer , Marker, Popup} from 'react-leaflet';

import mapMarkerImg from '../images/map-marker.svg'

import '../styles/pages/orphanages-map.css';
import mapIcon from "../utils/mapIcon";

import '../styles/pages/orphanages-map.css';
import api from '../services/api';

interface Orphanage {
    id: number;
    latitude: number;
    longitude: number;
    name:string;
}


function OrphanagesMap(){
    const [orphanages, setOrphanages] = useState<Orphanage[]>([]);

useEffect(()=>{
    api.get('orphanages').then(response=>{
    setOrphanages(response.data);
    })
},[]);

    return (
        <div id="page-map">
            <aside>
                <header>
                    <img src={mapMarkerImg} alt="Happy"/>

                    <h2>Escolha um orfanato no mapa</h2>
                    <p>Muitas crianças estão esperando a sua visita :)</p>
                </header>

                <footer>
                    <strong>Rio de Janeiro</strong>
                    <span>-RJ</span>
                </footer>
            </aside>

            <Map 
            center={[-22.9311479,-43.3584274]}
            zoom={15}
            style={{ width: '100%', height: '100%' }}
            >
                <TileLayer url={`https://api.mapbox.com/styles/v1/mapbox/light-v10/tiles/256/{z}/{x}/{y}@2x?access_token=${process.env.REACT_APP_MAPBOX_TOKEN}`}/>
                {console.log(orphanages[19])}
               {orphanages.map(orphanages=>{
                   return(
                    <Marker
                    key = {orphanages.id}
                    icon = {mapIcon}
                    position={[orphanages.latitude,orphanages.longitude]}

                >
                    <Popup closeButton={false} minWidth={240} className="map-popup">
                {orphanages.name}
                <Link to={`/orphanages/${orphanages.id}`}>
                    <FiArrowRight size={20} color="#FFF" />
                </Link>
            </Popup>
                </Marker>
                   )
               })}
            </Map>
            

            <Link to="/orphanages/create" className="create-orphanage">
                <FiPlus size={43} color ="#FFF" />
            </Link>
        </div>
    )
}

export default OrphanagesMap;
